SELECT IIf([Funkció]="-" And [Feladata]="12-Osztályvezetõi feladatok",IIf([Funkcionális]=True,"a) csoport","Egyéb (…) II"),[Funkció]) AS Funkciója, Choose(Left(IIf([Legmagasabb iskolai végzettség 1=8 osztály; 2=érettségi; 3=fõis] Is Null Or [Legmagasabb iskolai végzettség 1=8 osztály; 2=érettségi; 3=fõis]="",0,[Legmagasabb iskolai végzettség 1=8 osztály; 2=érettségi; 3=fõis]),1)*1,"alapfok","középfok","felsõfok","felsõfok","középfok","középfok") AS Végzettség, IIf([lkSzemélyek].[Besorolás2] Like "*osztály*" Or [lkSzemélyek].[Besorolás2] Like "*járási*" Or [lkSzemélyek].[Besorolás2] Like "*igazgató*" Or [lkSzemélyek].[Besorolás2] Like "fõispán","a Vezetõi létszám","b Nem vezetõi létszám") AS Vezetõ, Count(lkSzemélyek.Adójel) AS Létszám, lkSzemélyek.[jogviszony típusa / jogviszony típus] AS Jogviszony
FROM lkJárásiKormányKözpontosítottUnió RIGHT JOIN (tFunkcionálisSzakmaiFõosztályok RIGHT JOIN (lkFeladatKirafeladatFunkció RIGHT JOIN lkSzemélyek ON (lkFeladatKirafeladatFunkció.[KIRA feladat megnevezés] = lkSzemélyek.[KIRA feladat megnevezés]) AND (lkFeladatKirafeladatFunkció.Feladata = lkSzemélyek.Feladat)) ON tFunkcionálisSzakmaiFõosztályok.SzervezetKód = lkSzemélyek.FõosztályKód) ON lkJárásiKormányKözpontosítottUnió.Adójel = lkSzemélyek.Adójel
WHERE (((lkSzemélyek.[Státusz neve])="Álláshely") AND ((lkSzemélyek.[Tartós távollét típusa]) Is Null) AND ((lkSzemélyek.[Státusz típusa])="Szervezeti alaplétszám") AND ((lkSzemélyek.[Elméleti (szerzõdés/kinevezés szerinti) ledolgozandó havi óraker])>=60))
GROUP BY IIf([Funkció]="-" And [Feladata]="12-Osztályvezetõi feladatok",IIf([Funkcionális]=True,"a) csoport","Egyéb (…) II"),[Funkció]), Choose(Left(IIf([Legmagasabb iskolai végzettség 1=8 osztály; 2=érettségi; 3=fõis] Is Null Or [Legmagasabb iskolai végzettség 1=8 osztály; 2=érettségi; 3=fõis]="",0,[Legmagasabb iskolai végzettség 1=8 osztály; 2=érettségi; 3=fõis]),1)*1,"alapfok","középfok","felsõfok","felsõfok","középfok","középfok"), IIf([lkSzemélyek].[Besorolás2] Like "*osztály*" Or [lkSzemélyek].[Besorolás2] Like "*járási*" Or [lkSzemélyek].[Besorolás2] Like "*igazgató*" Or [lkSzemélyek].[Besorolás2] Like "fõispán","a Vezetõi létszám","b Nem vezetõi létszám"), lkSzemélyek.[jogviszony típusa / jogviszony típus];
