SELECT DISTINCT lkSzemélyek.Fõosztály, lkSzemélyek.Osztály, tIlletmények.Név, tIlletmények.[Adóazonosító jel], tIlletmények.[Átsorolás összesen] AS PGF, lkSzemélyek.[Kerekített 100 %-os illetmény (eltérített)] AS NEXON, lkSzemélyek.[Elméleti (szerzõdés/kinevezés szerinti) ledolgozandó heti óraker] AS [Heti óra], [Nexon]/[Heti óra]*40 AS [Nexon 40 óra], kt_azNexon_Adójel.Nlink AS Link, lkSzemélyek.[Státusz típusa], tIlletmények.[Jogviszony, juttatás típusa]
FROM (tIlletmények LEFT JOIN lkSzemélyek ON tIlletmények.[Adóazonosító jel]=lkSzemélyek.Adójel) LEFT JOIN kt_azNexon_Adójel ON lkSzemélyek.Adójel=kt_azNexon_Adójel.Adójel
WHERE (((lkSzemélyek.[Státusz típusa]) Is Not Null) AND ((tIlletmények.[Jogviszony, juttatás típusa])=20 Or (tIlletmények.[Jogviszony, juttatás típusa])=6 Or (tIlletmények.[Jogviszony, juttatás típusa])=18));
