SELECT (Select max(SorszámEng) From tEngedéllyelValóÖsszevetésTábla)-[SorszámEng]+(IIf([tEngedéllyelValóÖsszevetésTábla].[Zóna]="Alaplétszám",0,IIf([tEngedéllyelValóÖsszevetésTábla].[zóna]="Központosított",12,24))) AS Sorszám, tEngedéllyelValóÖsszevetésTábla.Magyarázat, tEngedéllyelValóÖsszevetésTábla.Zóna, tEngedéllyelValóÖsszevetésTábla.Besorolás_bemenet, tEngedéllyelValóÖsszevetésTábla.Engedélyezett, tEngedéllyelValóÖsszevetésTábla.Betöltött, tEngedéllyelValóÖsszevetésTábla.Üres, tEngedéllyelValóÖsszevetésTábla.[Összes álláshely], ([Nexonban]) AS [Nexonban összesen], ([Összes álláshely]-[Nexonban]) AS Eltérés
FROM lkLétszámBesorolásonkéntHavibólUNIÓ RIGHT JOIN tEngedéllyelValóÖsszevetésTábla ON (lkLétszámBesorolásonkéntHavibólUNIÓ.Zóna = tEngedéllyelValóÖsszevetésTábla.Zóna) AND (lkLétszámBesorolásonkéntHavibólUNIÓ.Besorolás = tEngedéllyelValóÖsszevetésTábla.Besorolás_bemenet);
