SELECT tMeghagyandókAránya.Azonosító, tMeghagyandókAránya.Fõosztály, lkSzervezetiBetöltések.FõosztályKód, lkSzervezetiBetöltések.[Szülõ szervezeti egységének kódja], 1 AS Létszám, tMeghagyandókAránya.Meghagyandó AS [Meghagyandó%], lkSzervezetiBetöltések.[Szervezetmenedzsment kód]
FROM lkSzemélyek INNER JOIN (tMeghagyandókAránya INNER JOIN lkSzervezetiBetöltések ON tMeghagyandókAránya.[Szervezeti egység kódja] = lkSzervezetiBetöltések.FõosztályKód) ON lkSzemélyek.[Adóazonosító jel] = lkSzervezetiBetöltések.[Szervezetmenedzsment kód]
WHERE (((lkSzemélyek.[Besorolási  fokozat (KT)])<>"Fõosztályvezetõ" And (lkSzemélyek.[Besorolási  fokozat (KT)])<>"Osztályvezetõ" And (lkSzemélyek.[Besorolási  fokozat (KT)])<>"Járási / kerületi hivatal vezetõje" And (lkSzemélyek.[Besorolási  fokozat (KT)])<>"Járási / kerületi hivatal vezetõjének helyettese" And (lkSzemélyek.[Besorolási  fokozat (KT)])<>"Fõispán" And (lkSzemélyek.[Besorolási  fokozat (KT)])<>"Kormányhivatal fõigazgatója" And (lkSzemélyek.[Besorolási  fokozat (KT)])<>"Kormányhivatal igazgatója") AND ((Year(Now())-Year([Születési idõ])) Between 18 And 50) AND ((lkSzemélyek.Neme)="férfi"));
