SELECT lkSzervezetekSzemélyekbõl.bfkh, lkSzervezetÁlláshelyek.SzervezetKód, lkSzervezetekSzemélyekbõl.Fõosztály, lkSzervezetekSzemélyekbõl.Osztály, lkSzervezetÁlláshelyek.álláshely, lkSzervezetÁlláshelyek.[Tervezett betöltési adatok - Elõmeneteli fokozat megnevezés], lkSzemélyek.[Dolgozó teljes neve], lkSzemélyek.[Besorolási  fokozat (KT)], lkSzemélyek.[Vezetõi beosztás megnevezése], lkSzemélyek.[Vezetõi megbízás típusa], lkSzemélyek.[Kerekített 100 %-os illetmény (eltérített)] AS Illetmény, lkSzemélyek.[Tartós távollét típusa], lkSzemélyek.[Tartós távollét vége], lkSzemélyek.[Tartós távollét tervezett vége], kt_azNexon_Adójel.NLink
FROM kt_azNexon_Adójel RIGHT JOIN (lkSzervezetekSzemélyekbõl RIGHT JOIN (lkSzervezetÁlláshelyek LEFT JOIN lkSzemélyek ON lkSzervezetÁlláshelyek.Álláshely=lkSzemélyek.[Státusz kódja]) ON lkSzervezetekSzemélyekbõl.[Szervezeti egység kódja]=lkSzervezetÁlláshelyek.SzervezetKód) ON kt_azNexon_Adójel.Adójel=lkSzemélyek.Adójel
WHERE (((lkSzemélyek.[Besorolási  fokozat (KT)])="Osztályvezetõ") AND ((lkSzemélyek.[Státusz neve])="Álláshely")) OR (((lkSzemélyek.[Vezetõi megbízás típusa])="Osztályvezetõ") AND ((lkSzemélyek.[Státusz neve])="Álláshely")) OR (((lkSzemélyek.[Vezetõi beosztás megnevezése])="Osztályvezetõ") AND ((lkSzemélyek.[Státusz neve])="Álláshely")) OR (((lkSzervezetÁlláshelyek.[Tervezett betöltési adatok - Elõmeneteli fokozat megnevezés])="osztályvezetõ"))
ORDER BY lkSzervezetekSzemélyekbõl.bfkh;
