SELECT DISTINCT Unió.Tábla, tSzervezetiEgységek.Fõosztály, Unió.Adóazonosító, Unió.[Álláshely azonosító], Unió.[ÁNYR SZERVEZETI EGYSÉG AZONOSÍTÓ], Unió.[Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ], Unió.[Heti munkaórák száma], IIf([Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ]="T" Or [Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ]="NYT",1,0) AS [Teljes munkaidõs], IIf([Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ]="T" Or [Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ]="NYT",0,1) AS Részmunkaidõs
FROM tSzervezetiEgységek RIGHT JOIN (SELECT "Kormányhivatali_állomány" AS Tábla , "Részmunkaidõsnek van jelölve, de teljes munkaidõben dolgozik." AS [Hibás érték] , Kormányhivatali_állomány.Adóazonosító, Kormányhivatali_állomány.[Álláshely azonosító] , Kormányhivatali_állomány.[ÁNYR SZERVEZETI EGYSÉG AZONOSÍTÓ] , Kormányhivatali_állomány.[Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ] , Kormányhivatali_állomány.[Heti munkaórák száma] , IIf(Right([Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ],1)="R" And [Heti munkaórák száma]=40,True,False) AS Hibás FROM Kormányhivatali_állomány  UNION SELECT "Járási_állomány" AS Tábla , "Részmunkaidõsnek van jelölve, de teljes munkaidõben dolgozik." AS [Hibás érték] , Járási_állomány.Adóazonosító , Járási_állomány.[Álláshely azonosító] , Járási_állomány.[ÁNYR SZERVEZETI EGYSÉG AZONOSÍTÓ] , Járási_állomány.[Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ] , Járási_állomány.[Heti munkaórák száma] , IIf(Right([Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ],1)="R" And [Heti munkaórák száma]=40,True,False) AS Hibás FROM Járási_állomány  UNION SELECT "Kormányhivatali_állomány" AS Tábla , "Teljes munkaidõsnek van jelölve, de részmunkaidõben dolgozik." AS [Hibás érték] , Kormányhivatali_állomány.Adóazonosító, Kormányhivatali_állomány.[Álláshely azonosító] , Kormányhivatali_állomány.[ÁNYR SZERVEZETI EGYSÉG AZONOSÍTÓ] , Kormányhivatali_állomány.[Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ] , Kormányhivatali_állomány.[Heti munkaórák száma] , IIf(Right([Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ],1)="T" And [Heti munkaórák száma]<>40,True,False) AS Hibás FROM Kormányhivatali_állomány  UNION SELECT "Járási_állomány" AS Tábla , "Teljes munkaidõsnek van jelölve, de részmunkaidõben dolgozik." AS [Hibás érték] , Járási_állomány.Adóazonosító , Járási_állomány.[Álláshely azonosító] , Járási_állomány.[ÁNYR SZERVEZETI EGYSÉG AZONOSÍTÓ] , Járási_állomány.[Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ] , Járási_állomány.[Heti munkaórák száma] , IIf(Right([Foglalkoztatási forma teljes (T) / részmunkaidõs (R), nyugdíjas ],1)="T" And [Heti munkaórák száma]<>40,True,False) AS Hibás FROM Járási_állomány  )  AS Unió ON tSzervezetiEgységek.[Szervezeti egység kódja]=Unió.[ÁNYR SZERVEZETI EGYSÉG AZONOSÍTÓ]
WHERE (((Len([Adóazonosító]))>"0"));
