SELECT tSzervezeti.OSZLOPOK, bfkh([Szülõ szervezeti egységének kódja]) AS SzervezetKód, tSzervezeti.[Szervezetmenedzsment kód] AS ÁlláshelyAzonosító, IIf([Szervezeti egységének szintje]=7 And [Szint3 - kód]="",[Szülõ szervezeti egységének kódja],IIf([Szint6 - kód]="",IIf([Szint5 - kód]="",IIf([Szint4 - kód]="",IIf([Szint3 - kód]="",[Szint2 - kód],[Szint3 - kód]),[Szint4 - kód]),[Szint5 - kód]),[Szint6 - kód])) AS FõosztályKód, tSzervezeti.[Megnevezés szótárelem kódja], tSzervezeti.Név, tSzervezeti.[Érvényesség kezdete], tSzervezeti.[Érvényesség vége], tSzervezeti.[Költséghely kód], tSzervezeti.[Költséghely megnevezés], tSzervezeti.[Szervezeti egységének szintje], tSzervezeti.[Szülõ szervezeti egységének kódja], tSzervezeti.[Szervezeti egységének megnevezése], tSzervezeti.[Szervezeti egységének vezetõje], tSzervezeti.[Szervezeti egységének vezetõjének azonosítója], tSzervezeti.[A költséghely eltér a szervezeti egységének költséghelytõl?], tSzervezeti.[Szervezeti munkakörének kódja], tSzervezeti.[Szervezeti munkakörének megnevezése], tSzervezeti.[A költséghely eltér a szervezeti munkakörének költséghelyétõl?], tSzervezeti.[Vezetõi státusz], tSzervezeti.[Helyettes vezetõ-e], tSzervezeti.[Tervezett betöltési adatok - Jogviszony típus], tSzervezeti.[Tervezett betöltési adatok - Kulcsszám kód], tSzervezeti.[Tervezett betöltési adatok - Kulcsszám megnevezés], tSzervezeti.[Tervezett betöltési adatok - Elõmeneteli fokozat kód], tSzervezeti.[Tervezett betöltési adatok - Elõmeneteli fokozat megnevezés], tSzervezeti.[Pályáztatás határideje], tSzervezeti.[Vezetõi beosztás KT], tSzervezeti.[Pályáztatás alatt áll], tSzervezeti.Megjegyzés, tSzervezeti.[Státusz engedélyezett óraszáma], tSzervezeti.[Státusz engedélyezett FTE (üzleti paraméter szerint számolva)], tSzervezeti.[Átmeneti óraszám], tSzervezeti.[Átmeneti létszám (FTE)], tSzervezeti.[Közzétett hierarchiában megjelenítendõ], tSzervezeti.[Asszisztens státusz], tSzervezeti.[Létszámon felül létrehozott státusz], tSzervezeti.[Státusz típusa], tSzervezeti.[Státusz betöltési óraszáma], tSzervezeti.[Státusz betöltési FTE], tSzervezeti.[Státusz betöltési óraszáma minusz státusz engedélyezett óraszáma], tSzervezeti.[Státusz betöltési FTE minusz státusz engedélyezett FTE], tSzervezeti.[Mióta betöltetlen a státusz (dátum)], tSzervezeti.[Hány napja betöltetlen (munkanap, alapnaptár alapján)], tSzervezeti.[Szint1 - kód], tSzervezeti.[Szint1 - leírás], tSzervezeti.[Szint2 - kód], tSzervezeti.[Szint2 - leírás], tSzervezeti.[Szint3 - kód], tSzervezeti.[Szint3 - leírás], tSzervezeti.[Szint4 - kód], tSzervezeti.[Szint4 - leírás], tSzervezeti.[Szint5 - kód], tSzervezeti.[Szint5 - leírás], tSzervezeti.[Szint6 - kód], tSzervezeti.[Szint6 - leírás], tSzervezeti.[Szint7 - kód], tSzervezeti.[Szint7 - leírás], tSzervezeti.[Szint8 - kód], tSzervezeti.[Szint8 - leírás]
FROM tSzervezeti
WHERE (((tSzervezeti.[Szervezetmenedzsment kód]) Like "S-*") AND ((tSzervezeti.[Érvényesség kezdete])<=Date()) AND ((tSzervezeti.[Érvényesség vége])>=Date() Or (tSzervezeti.[Érvényesség vége])=0));
