SELECT DISTINCT Nz([lkSzemélyek].[Fõosztály],Nz([lkSzemélyek_1].[Fõosztály],"-")) AS Fõosztály, Nz([lkSzemélyek].[Osztály],Nz([lkSzemélyek_1].[Osztály],"-")) AS Osztály, kt_azNexon_Adójel.Név AS Név, t__Ellenõrzés_02.Hiányzó_érték AS [Hiányzó érték], t__Ellenõrzés_02.[Álláshely azonosító] AS [Státusz kód], kt_azNexon_Adójel.NLink AS NLink, tJavítandóMezõnevek.Elérés AS Megjegyzés
FROM lkSzemélyek AS lkSzemélyek_1 RIGHT JOIN (lkSzemélyek RIGHT JOIN (tJavítandóMezõnevek RIGHT JOIN (kt_azNexon_Adójel RIGHT JOIN t__Ellenõrzés_02 ON kt_azNexon_Adójel.Adójel = t__Ellenõrzés_02.Adójel) ON tJavítandóMezõnevek.Eredeti = t__Ellenõrzés_02.Hiányzó_érték) ON lkSzemélyek.Adójel = t__Ellenõrzés_02.Adójel) ON lkSzemélyek_1.[Státusz kódja] = t__Ellenõrzés_02.[Álláshely azonosító]
GROUP BY Nz([lkSzemélyek].[Fõosztály],Nz([lkSzemélyek_1].[Fõosztály],"-")), Nz([lkSzemélyek].[Osztály],Nz([lkSzemélyek_1].[Osztály],"-")), kt_azNexon_Adójel.Név, t__Ellenõrzés_02.Hiányzó_érték, t__Ellenõrzés_02.[Álláshely azonosító], kt_azNexon_Adójel.NLink, tJavítandóMezõnevek.Elérés, t__Ellenõrzés_02.Adójel;
