SELECT DISTINCT Nz([lkSzemélyek].[Fõosztály],Nz([lkSzemélyek_1].[Fõosztály],"-")) AS Fõoszt, t__Ellenõrzés_02.Hiányzó_érték AS Hiányzó_érték, t__Ellenõrzés_02.Adójel AS Adójel, kt_azNexon_Adójel.Név AS Név, t__Ellenõrzés_02.[Álláshely azonosító] AS [Álláshely azonosító], kt_azNexon_Adójel.NLink, tJavítandóMezõnevek.Elérés AS Megjegyzés
FROM lkSzemélyek AS lkSzemélyek_1 RIGHT JOIN (lkSzemélyek RIGHT JOIN (tJavítandóMezõnevek RIGHT JOIN (kt_azNexon_Adójel RIGHT JOIN t__Ellenõrzés_02 ON kt_azNexon_Adójel.Adójel=t__Ellenõrzés_02.Adójel) ON tJavítandóMezõnevek.Eredeti=t__Ellenõrzés_02.Hiányzó_érték) ON lkSzemélyek.Adójel=t__Ellenõrzés_02.Adójel) ON lkSzemélyek_1.[Státusz kódja]=t__Ellenõrzés_02.[Álláshely azonosító]
GROUP BY Nz([lkSzemélyek].[Fõosztály],Nz([lkSzemélyek_1].[Fõosztály],"-")), t__Ellenõrzés_02.Hiányzó_érték, t__Ellenõrzés_02.Adójel, kt_azNexon_Adójel.Név, t__Ellenõrzés_02.[Álláshely azonosító], kt_azNexon_Adójel.NLink, tJavítandóMezõnevek.Elérés;
