SELECT lk__BesorolásokHavi_vs_Ányr01.Fõosztály AS Fõosztály, lk__BesorolásokHavi_vs_Ányr01.Szervezet AS Osztály, lkSzemélyek.[Dolgozó teljes neve] AS Név, lk__BesorolásokHavi_vs_Ányr01.[Álláshely azonosító] AS [Státusz kód], lk__BesorolásokHavi_vs_Ányr01.NexonHavi AS [Nexon havi], lk__BesorolásokHavi_vs_Ányr01.ÁNYR AS ÁNYR, lk__BesorolásokHavi_vs_Ányr01.[Besorolási fokozat kód:] AS [Besorolás kód], kt_azNexon_Adójel02.NLink AS NLink
FROM (lkSzemélyek RIGHT JOIN lk__BesorolásokHavi_vs_Ányr01 ON lkSzemélyek.[Státusz kódja] = lk__BesorolásokHavi_vs_Ányr01.[Álláshely azonosító]) LEFT JOIN kt_azNexon_Adójel02 ON lkSzemélyek.Adójel = kt_azNexon_Adójel02.Adójel
ORDER BY lk__BesorolásokHavi_vs_Ányr01.BFKH;
