SELECT lkBelépõkUnió.Adóazonosító AS Adójel, lkSzemélyekMind.[Dolgozó teljes neve], Nz([lkBelépõkUnió].[Fõosztály],[lkSzemélyekMind].[Fõosztály]) AS Fõoszt, Nz([lkBelépõkUnió].[Osztály],[lkSzemélyekMind].[Osztály]) AS Oszt, ffsplit([Feladatkör],"-",2) AS [Ellátandó feladat], lkSzemélyekMind.[Jogviszony kezdete (belépés dátuma)] AS Belépés, lkSzemélyekMind.[Jogviszony vége (kilépés dátuma)] AS Kilépés
FROM lkSzemélyekMind INNER JOIN lkBelépõkUnió ON (lkSzemélyekMind.Adójel = lkBelépõkUnió.Adójel) AND (lkSzemélyekMind.[Jogviszony kezdete (belépés dátuma)] = lkBelépõkUnió.[Jogviszony kezdõ dátuma])
WHERE (((Nz([lkBelépõkUnió].[Fõosztály],[lkSzemélyekMind].[Fõosztály])) Like "Humán*") AND ((ffsplit([Feladatkör],"-",2))<>"") AND ((lkSzemélyekMind.[Jogviszony kezdete (belépés dátuma)])<=[Az idõszak vége]) AND ((lkSzemélyekMind.[Jogviszony vége (kilépés dátuma)])>=[Az idõszak kezdete]));
