SELECT Replace(IIf([4 szint]="",IIf([3 szint]="",IIf([2 szint]="",[1 szint],[2 szint]),[3 szint]),[4 szint]),"Budapest Fõváros Kormányhivatala ","BFKH ") AS Fõosztály, Álláshelyek.[5 szint] AS Osztály, Nz(Switch([Fõosztály]="Védelmi bizottság titkársága","Fõispán",[Fõosztály]="Fõigazgatói titkárság","Fõigazgató",[Fõosztály]="Belsõ Ellenõrzési Osztály","Fõispán"),[Fõosztály]) AS Fõoszt, Nz(Switch([Fõosztály]="Védelmi bizottság titkársága",[Fõosztály],[Fõosztály]="Fõigazgatói titkárság",[Fõosztály],[Fõosztály]="Belsõ Ellenõrzési Osztály",[Fõosztály]),[Osztály]) AS Oszt, Álláshelyek.*, tBesorolásÁtalakítóEltérõBesoroláshoz.[Besorolási  fokozat (KT)], tBesorolásÁtalakítóEltérõBesoroláshoz.rang, tBesorolásÁtalakítóEltérõBesoroláshoz.jel, IIf([Álláshely státusza]="betöltetlen","ÜÁ." & [jel],IIf([Álláshelyen fennálló jogviszony] Like "Munka*","Mt." & [jel],[jel])) AS jel2
FROM tBesorolásÁtalakítóEltérõBesoroláshoz RIGHT JOIN Álláshelyek ON tBesorolásÁtalakítóEltérõBesoroláshoz.[Álláshely besorolási kategóriája] = Álláshelyek.[Álláshely besorolási kategóriája];
