SELECT lkSzemélyek.[Dolgozó teljes neve], lkSzemélyek.[Dolgozó születési neve], [Születési hely] & ", " & [Születési idõ] AS [Születési hely idõ], lkSzemélyek.[Anyja neve], lk_Garantált_bérminimum_alatti_01.Adójel, lkSzemélyek.[TAJ szám], Replace([Állandó lakcím],"Magyarország, ","") AS Lakcím, lkSzemélyek.Azonosító AS Kif1, "" AS Iktatószám, lk_Garantált_bérminimum_alatti_01.Illetmény, lk_Garantált_bérminimum_alatti_01.[Heti munkaórák száma], lk_Garantált_bérminimum_alatti_01.[Álláshely azonosító], lk_Garantált_bérminimum_alatti_01.Bruttó_bér, lk_Garantált_bérminimum_alatti_01.[Álláshely azonosító], lkSzemélyek.[Státusz típusa], lkSzemélyek.[KIRA jogviszony jelleg], lkSzemélyek.Fõosztály, lkSzemélyek.Osztály, lkSzemélyek.[Iskolai végzettség foka], lkSzemélyek.[Iskolai végzettség neve]
FROM lk_Garantált_bérminimum_alatti_01 LEFT JOIN lkSzemélyek ON lk_Garantált_bérminimum_alatti_01.Adójel = lkSzemélyek.Adójel
WHERE (((lk_Garantált_bérminimum_alatti_01.Garantált_min_alatt)=Yes))
ORDER BY lkSzemélyek.[Dolgozó teljes neve];
