SELECT Replace(IIf([4 szint]="",IIf([3 szint]="",IIf([2 szint]="",[1 szint],[2 szint]),[3 szint]),[4 szint]),"Budapest Fõváros Kormányhivatala ","BFKH ") AS Fõosztály, Álláshelyek.*, tBesorolásÁtalakítóEltérõBesoroláshoz.[Besorolási  fokozat (KT)], tBesorolásÁtalakítóEltérõBesoroláshoz.rang, tBesorolásÁtalakítóEltérõBesoroláshoz.jel, IIf([Álláshely státusza]="betöltetlen","ÜÁ." & [jel],IIf([Álláshelyen fennálló jogviszony] Like "Munka*","Mt." & [jel],[jel])) AS jel2
FROM tBesorolásÁtalakítóEltérõBesoroláshoz INNER JOIN Álláshelyek ON tBesorolásÁtalakítóEltérõBesoroláshoz.[Álláshely besorolási kategóriája]=Álláshelyek.[Álláshely besorolási kategóriája];
